sys_variable_value.value:
(function execute(inputs, outputs) {
  if(inputs['status']=="200"){
    var response = JSON.parse(inputs['response']);
    var users=[];
    for(var i=0;i<response.length;i++){
     	users.push(response[i].username);
    }
  }
    outputs['user'] = users;
})(inputs, outputs);

